import React, { useState } from "react";
import { DndContext } from "@dnd-kit/core";
import { Draggable } from "../components/DragClothes";
import { Droppable } from "../components/DropClothes";
import ClothesList from "../components/ClothesList.js";
import ClothesDrawer from "../components/ClothesDrawer.js";

const OutfitCreationPage = () => {
  const [isDrawerOpen, setIsDrawerOpen] = useState(false);
  const [isDropped, setIsDropped] = useState(false);
  const draggableMarkup = <Draggable>Drag me</Draggable>;

  return (
    <>
      <DndContext onDragEnd={handleDragEnd}>
        {!isDropped ? draggableMarkup : null}
        <Droppable>{isDropped ? draggableMarkup : "Drop here"}</Droppable>
      </DndContext>
      <button
        onClick={() => setIsDrawerOpen(true)}
        className="fixed top-4 left-1/2 -translate-x-1/2 rounded-md bg-black text-white px-4 py-2 shadow-lg hover:bg-zinc-800 transition z-[50]"
      >
        Open drawer
      </button>
      <ClothesDrawer
        isOpen={isDrawerOpen}
        onClose={() => setIsDrawerOpen(false)}
      >
        <div className="text-white">
          <h3 className="text-lg font-semibold mb-4">Drawer Content</h3>
          <p className="text-gray-300 mb-4">
            This is a pure React and Tailwind drawer component. It slides in
            from the right side of the screen.
          </p>

          <div className="space-y-4">
            <div className="bg-gray-700 p-4 rounded-lg">
              <h4 className="font-medium mb-2">Feature 1</h4>
              <p className="text-sm text-gray-400">
                Smooth slide-in animation with Tailwind transitions
              </p>
            </div>

            <div className="bg-gray-700 p-4 rounded-lg">
              <h4 className="font-medium mb-2">Feature 2</h4>
              <p className="text-sm text-gray-400">
                Allows interaction with background elements
              </p>
            </div>

            <div className="bg-gray-700 p-4 rounded-lg">
              <h4 className="font-medium mb-2">Feature 3</h4>
              <p className="text-sm text-gray-400">Scrollable content area</p>
            </div>

            <div className="bg-gray-700 p-4 rounded-lg">
              <h4 className="font-medium mb-2">Feature 4</h4>
              <p className="text-sm text-gray-400">
                Responsive design that works on mobile and desktop
              </p>
            </div>
          </div>

          <button
            onClick={() => setIsDrawerOpen(false)}
            className="mt-6 w-full bg-indigo-600 text-white px-4 py-2 rounded-lg hover:bg-indigo-700 transition-colors"
          >
            Close Drawer
          </button>
        </div>
      </ClothesDrawer>
    </>
  );
  function handleDragEnd(event) {
    if (event.over && event.over.id === "droppable") {
      setIsDropped(true);
    }
  }
};

export default OutfitCreationPage;
