import React, { useState } from "react";
import { DndContext } from "@dnd-kit/core";
import { Draggable } from "../components/DragClothes";
import { Droppable } from "../components/DropClothes";
import DroppedClothing from "../components/DroppedClothing.js";
import { XMarkIcon } from "@heroicons/react/24/outline";
import ClothesList from "../components/ClothesList.js";
import {
  Dialog,
  DialogBackdrop,
  DialogPanel,
  DialogTitle,
  TransitionChild,
} from "@headlessui/react";

const OutfitCreationPage = () => {
  const [isDropped, setIsDropped] = useState(false);
  const [droppedItems, setDroppedItems] = useState([]);
  const [open, setOpen] = useState(false);
  const draggableMarkup = (
    <Draggable>
      <ClothesList
        clothingtypeID="tops"
        isDraggablePage={true}
        cardClick={false}
      />
    </Draggable>
  );

  return (
    <>
      <button
        onClick={() => setOpen(true)}
        className="fixed top-4 left-1/2 -translate-x-1/2 rounded-md bg-black text-white px-4 py-2 shadow-lg hover:bg-zinc-800 transition z-[50]"
      >
        Open drawer
      </button>
      <div className="grid grid-cols-4 w-full min-h-screen">
        <div className="col-span-4 bg-zinc-400 p-4">
          <h1>Outfit Board</h1>

          <DndContext onDragEnd={handleDragEnd}>
            {!isDropped ? draggableMarkup : null}
            <Droppable>{isDropped ? draggableMarkup : "Drop here"}</Droppable>
          </DndContext>
        </div>
      </div>

      {/* ---------- DIALOG / DRAWER (fixed + non-blocking overlay) ---------- */}
      <Dialog open={open} onClose={setOpen} className="relative z-[60]">
        {/* Backdrop: visible but does NOT block pointer events */}
        <DialogBackdrop className="pointer-events-none fixed inset-0 bg-gray-900/50" />

        {/* Outer fixed container: pointer-events-none so underlying UI stays clickable */}
        <div className="fixed inset-0 overflow-hidden pointer-events-none">
          <div className="absolute inset-0 overflow-hidden pointer-events-none">
            {/* Panel container: still pointer-events-none so the sliding area doesn't block clicks
                only the DialogPanel itself will accept pointer events */}
            <div className="pointer-events-none fixed inset-y-0 right-0 flex max-w-full pl-10 sm:pl-16">
              {/* The actual interactive panel: pointer-events-auto */}
              <DialogPanel className="pointer-events-auto relative w-screen max-w-md transform transition duration-500 ease-in-out">
                {/* close button and header */}
                <div className="relative flex h-full flex-col overflow-y-auto bg-gray-800 py-6 shadow-xl after:absolute after:inset-y-0 after:left-0 after:w-px after:bg-white/10">
                  <div className="px-4 sm:px-6 flex items-start justify-between">
                    <DialogTitle className="text-base font-semibold text-white">
                      Panel title
                    </DialogTitle>

                    <button
                      type="button"
                      onClick={() => setOpen(false)}
                      className="rounded-md text-gray-400 hover:text-white focus:outline-none"
                    >
                      <span className="sr-only">Close panel</span>
                      <XMarkIcon aria-hidden="true" className="h-6 w-6" />
                    </button>
                  </div>

                  <div className="relative mt-6 flex-1 px-4 sm:px-6">
                    <p className="text-white mb-4">Your Clothes</p>
                    <ClothesList
                      clothingtypeID="tops"
                      isDraggablePage={true}
                      cardClick={false}
                    />
                  </div>
                </div>
              </DialogPanel>
            </div>
          </div>
        </div>
      </Dialog>
    </>
  );
  function handleDragEnd(event) {
    const { active, over } = event;

    if (over && over.id === "droppable") {
      setDroppedItems((prev) => [...prev, { id: active.id }]);
    }
  }
};

export default OutfitCreationPage;
