import { useState } from "react";
import CreatableSelect from "react-select/creatable";
import Select from "react-select";
import {
  clothingtypeOptions,
  colorOptions,
  seasonOptions,
  occasionOptions,
  brandOptions,
  sizeOptions,
  materialOptions,
  patternOptions,
} from "../option_data/data.ts";

const ClothesEdit = ({ clothe, onSubmit }) => {
  const convertToOption = (value, options) => {
    if (!value) return null;
    return options.find((opt) => opt.value === value) || null;
  };

  const convertToMultiOption = (values, options) => {
    if (!Array.isArray(values)) return [];
    return values
      .map((val) => options.find((opt) => opt.value === val))
      .filter(Boolean);
  };

  const [clothingtype, setClothingType] = useState(
    convertToOption(clothe.categorytype, clothingtypeOptions)
  );
  const [color, setColor] = useState(
    convertToMultiOption(clothe.color, colorOptions)
  );
  const [season, setSeason] = useState(
    convertToMultiOption(clothe.season, seasonOptions)
  );
  const [occasion, setOccasion] = useState(
    convertToMultiOption(clothe.occasion, occasionOptions)
  );
  const [brand, setBrand] = useState(
    convertToOption(clothe.brand, brandOptions)
  );
  const [size, setSize] = useState(convertToOption(clothe.size, sizeOptions));
  const [material, setMaterial] = useState(
    convertToMultiOption(clothe.material, materialOptions)
  );
  const [pattern, setPattern] = useState(
    convertToMultiOption(clothe.pattern, patternOptions)
  );

  const formatValue = (val) =>
    val ? (Array.isArray(val) ? val.map((v) => v.value) : val.value) : "";

  const handleSubmit = (event) => {
    event.preventDefault();

    onSubmit(clothe.id, {
      clothingtype: formatValue(clothingtype),
      color: formatValue(color),
      season: formatValue(season),
      occasion: formatValue(occasion),
      brand: formatValue(brand),
      size: formatValue(size),
      material: formatValue(material),
      pattern: formatValue(pattern),
    });
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <Select
          className="basic-single pb-4"
          classNamePrefix="select"
          name="Clothing Type"
          options={clothingtypeOptions}
          onChange={(value) => setClothingType(value)}
          value={clothingtype}
        />
        <CreatableSelect
          isMulti
          name="Color"
          options={colorOptions}
          className="basic-multi-select pb-4"
          classNamePrefix="select"
          onChange={(value) => setColor(value)}
          value={color}
        />
        <CreatableSelect
          isMulti
          name="Season"
          options={seasonOptions}
          className="basic-multi-select pb-4"
          classNamePrefix="select"
          onChange={(value) => setSeason(value)}
          value={season}
        />
        <CreatableSelect
          isMulti
          name="Occasion"
          options={occasionOptions}
          className="basic-multi-select pb-4"
          classNamePrefix="select"
          onChange={(value) => setOccasion(value)}
          value={occasion}
        />
        <Select
          className="basic-single pb-4"
          classNamePrefix="select"
          name="Brand"
          options={brandOptions}
          onChange={(value) => setBrand(value)}
          value={brand}
        />
        <Select
          className="basic-single pb-4"
          classNamePrefix="select"
          name="Size"
          options={sizeOptions}
          onChange={(value) => setSize(value)}
          value={size}
        />
        <CreatableSelect
          isMulti
          name="Material"
          options={materialOptions}
          className="basic-multi-select pb-4"
          classNamePrefix="select"
          onChange={(value) => setMaterial(value)}
          value={material}
        />
        <CreatableSelect
          isMulti
          name="Pattern"
          options={patternOptions}
          className="basic-multi-select pb-4"
          classNamePrefix="select"
          onChange={(value) => setPattern(value)}
          value={pattern}
        />
        <button
          type="submit"
          className="mt-4 px-4 py-2 bg-black text-white rounded"
        >
          Save Changes
        </button>
      </form>
    </>
  );
};

export default ClothesEdit;
