import React, { useState } from "react";
import { DragDropProvider } from "@dnd-kit/react";
import { Draggable } from "../components/DragClothes";
import { Droppable } from "../components/DropClothes";

const OutfitCreationPage = () => {
  const [isDropped, setIsDropped] = useState(false);

  return (
    <>
      <h1>Create Outfit</h1>
      <DragDropProvider
        onDragEnd={(event) => {
          if (event.canceled) return;

          const { target } = event.operation;
          setIsDropped(target?.id === "droppable");
        }}
      >
        {!isDropped && <Draggable />}

        <Droppable id="droppable">{isDropped && <Draggable />}</Droppable>
      </DragDropProvider>
    </>
  );
};

export default OutfitCreationPage;
