import { useState, useEffect } from "react";
import axios from "axios";
import TodoCreate from "./components/TodoCreate";
import TodoList from "./components/TodoList";
import { ReactComponent as GoogleIcon } from "./path/to/google.svg";

function App() {
  const [todos, setTodos] = useState([]);

  const fetchTodos = async () => {
    const response = await axios.get("http://localhost:3001/todos");
    setTodos(response.data);
  };
  // NEVER EVER NO NO
  // fetchTodos()
  useEffect(() => {
    fetchTodos();
  }, []);

  const createTodo = async (title) => {
    const response = await axios.post("http://localhost:3001/todos", {
      title,
    });

    const updatedTodos = [...todos, response.data];
    setTodos(updatedTodos);
  };

  const deleteTodoById = async (id) => {
    // delete from DB
    await axios.delete(`http://localhost:3001/todos/${id}`);
    // update our local state theb same way we did
    const updatedTodos = todos.filter((todo) => {
      return todo.id !== id;
    });
    setTodos(updatedTodos);
  };

  const editTodoById = async (id, newTitle) => {
    const response = await axios.put(`http://localhost:3001/todos/${id}`, {
      title: newTitle,
    });
    const updatedTodos = todos.map((todo) => {
      if (todo.id === id) {
        return {
          ...todo,
          ...response.data,
        };
      }
      return todo;
    });

    setTodos(updatedTodos);
  };

  return (
    <div>
      <TodoCreate onCreate={createTodo} />
      <TodoList todos={todos} onDelete={deleteTodoById} onEdit={editTodoById} />
      <svg xmlns="http://www.w3.org/2000/svg" height="48px" viewBox="0 -960 960 960" width="48px" fill="#FFFFFF"><path d="M450-450H200v-60h250v-250h60v250h250v60H510v250h-60v-250Z"/></svg>
    </div>
  );
}

export default App;
