import { useState } from "react";
import html2canvas from "html2canvas";
import { Cloudinary } from "@cloudinary/url-gen";


const CaptureScreenshot = async (element, imageFileName) => {
  const [imageSelected, setImageSelected] = useState(null);

  const cloud = new Cloudinary({
    cloud: { cloudName: "dmjzidsiy" }, //getting my name from Cloudinary
  });

  const formData = new FormData();
  formData.append("file", imageSelected);
  formData.append("upload_preset", "clothes_preset");

  const response = await axios.post(
    "https://api.cloudinary.com/v1_1/dmjzidsiy/image/upload",
    formData
  );

  const canvas = await html2canvas(element, {
    useCORS: true,
    allowTaint: true,
    backgroundColor: "#ffffff",
    scale: 2,
    logging: false,
  });
  const image = canvas.toDataURL("image/png", 1.0);
  downloadImage(image, imageFileName);
};
const downloadImage = (blob, fileName) => {
  const fakeLink = window.document.createElement("a");
  fakeLink.style = "display:none;";
  fakeLink.download = fileName;

  fakeLink.href = blob;

  document.body.appendChild(fakeLink);
  fakeLink.click();
  document.body.removeChild(fakeLink);

  fakeLink.remove();
};

export default CaptureScreenshot;
